\begin{tikzpicture}[scale=1, font=\footnotesize, line join=round, line cap=round, >=stealth]
	\def\a{1}	\def\b{1}	\def\c{1}	\def\d{-1}
	\def\tcn{\a/\c}\def\tcd{-\d/\c}
	\draw[color=gray,dash pattern=on 1pt off 1pt,xstep=1.0cm,ystep=1.0cm] (-5.2,-5.2) grid (5.2,5.2);
	\draw[->] (-5,0) -- (5,0) node[below] {$x$};
	\draw[->] (0,-5) -- (0,5) node[left] {$y$};
	\fill (0,0) circle (1pt) node[below left]{$O$};
	\draw[thick,dashed,blue] (\tcd,-5)--(\tcd,5) (-5,\tcn)--(5,\tcn);
	\clip (-5,-5)rectangle(5,5);
	\pgfmathsetmacro{\can}{-(\d)/(\c)}
	\draw[magenta,thick,samples=150,smooth,domain=-5:{\can-.1}] plot(\x,{(\a*\x+(\b))/(\c*\x+(\d))});
	\draw[magenta,thick,samples=150,smooth,domain={\can+.1}:5] plot(\x,{(\a*\x+(\b))/(\c*\x+(\d))}); 
\end{tikzpicture}

\begin{tikzpicture}[scale=1, font=\footnotesize, line join=round, line cap=round, >=stealth]
	% Định nghĩa các hệ số của hàm số
	\def\a{1}	\def\b{0}	\def\c{1}	\def\d{2}	\def\e{-1}	
	% Xác định tiệm cận đứng
	\pgfmathsetmacro{\tcd}{-\e/\d}	
	% Xác định hệ số tự do của tiệm cận xiên
	\pgfmathsetmacro{\intercept}{(\b*\d - \e *\a)/\d^2}	
	% Vẽ lưới và trục tọa độ
	\draw[color=gray, dash pattern=on 1pt off 1pt, xstep=1.0cm, ystep=1.0cm] (-5.2, -5.2) grid (5.2, 5.2);
	\draw[->] (-5, 0) -- (5, 0) node[below] {$x$};
	\draw[->] (0, -5) -- (0, 5) node[left] {$y$};
	\fill (0, 0) circle (1pt) node[below left]{$O$};
	% Vẽ các đường tiệm cận đứng
	\draw[thick, dashed, blue] (\tcd, -5) -- (\tcd, 5) node[above] {$x=\tcd$};	
	% Vẽ tiệm cận xiên
	\draw[thick, dashed, red] (-5, {(-5)*(\a/\d) + \intercept}) -- (5, {5*(\a/\d) + \intercept}) node[below right] {$y = \dfrac{\a}{\d}x + \intercept $};
	% Cắt đoạn đồ thị trong khung
	\clip (-5, -5) rectangle (5, 5);
	% Vẽ đồ thị hàm số
	\draw[magenta, thick, samples=150, smooth, domain=-5:{\tcd-.1}] plot (\x, {(\a*\x*\x + \b*\x + \c)/(\d*\x + \e)});
	\draw[magenta, thick, samples=150, smooth, domain={\tcd+.1}:5] plot (\x, {(\a*\x*\x + \b*\x + \c)/(\d*\x + \e)});

\end{tikzpicture}

