	% Định nghĩa các tỉ lệ phần trăm
	\def\A{10}
	\def\B{20}
	\def\C{30}
	\def\D{40}

	% Định nghĩa các góc tương ứng
	\pgfmathsetmacro{\angleA}{3.6*\A}
	\pgfmathsetmacro{\angleB}{3.6*\B}
	\pgfmathsetmacro{\angleC}{3.6*\C}
	\pgfmathsetmacro{\angleD}{3.6*\D}

	% Bán kính của hình tròn
	\def\radius{3}

	% Vẽ các phần của biểu đồ
	\fill[blue!50] (0,0) -- (\angleA:\radius) arc[start angle=\angleA, end angle=0, radius=\radius] -- cycle;
	\fill[red!50] (0,0) -- (\angleA:\radius) arc[start angle=\angleA, end angle=\angleA+\angleB, radius=\radius] -- cycle;
	\fill[green!50] (0,0) -- (\angleA+\angleB:\radius) arc[start angle=\angleA+\angleB, end angle=\angleA+\angleB+\angleC, radius=\radius] -- cycle;
	\fill[yellow!50] (0,0) -- (\angleA+\angleB+\angleC:\radius) arc[start angle=\angleA+\angleB+\angleC, end angle=360, radius=\radius] -- cycle;

	% Thêm nhãn phần trăm cho các phần bên trong
	\node at (\angleA/2:\radius/2) {\A\%};
	\node at (\angleA+\angleB/2:\radius/2) {\B\%};
	\node at (\angleA+\angleB+\angleC/2:\radius/2) {\C\%};
	\node at (\angleA+\angleB+\angleC+\angleD/2:\radius/2) {\D\%};
% Thêm nhãn tên phần ở trung điểm mỗi cung bên ngoài
\node at (\angleA/2:\radius + 0.25) {A};
\node at (\angleA+\angleB/2:\radius + 0.25) {B};
\node at (\angleA+\angleB+\angleC/2:\radius + 0.25) {C};
\node at (\angleA+\angleB+\angleC+\angleD/2:\radius + 0.25) {D};
\end{tikzpicture}

