Tree diagam5.content:
\begin{tikzpicture}
		% Node styles
		\tikzstyle{main node} = [circle, draw, fill=blue!30]
		\tikzstyle{branch node} = [circle, draw, fill=green!30]
		\tikzstyle{leaf node} = [circle, draw, fill=red!30]

		% Central node
		\node[main node] (0) {Tư duy chính};

		% First level nodes
		\foreach \i in {1,2,3} {
			\node[branch node] (a\i) at (120*\i-90:3cm) {Ý tưởng \i};
			\draw[->] (0) -- (a\i);
		}

		% Second level nodes
		\foreach \i/\j in {1/1,1/2,2/1,2/2,3/1,3/2} {
			\node[leaf node] (b\i\j) at (120*\i-90 + 40*\j-20:5cm) {Chi tiết \i.\j};
			\draw[->] (a\i) -- (b\i\j);
		}
	\end{tikzpicture}

